 const ContextUser=React.createContext();
function App() {
  const [siteLanguage, setsiteLanguage] = useState('en');
  const handlesiteName=()=>{
    setsiteLanguage()
  }
  return (
    <div className="App">
      <ContextUser.Provider value={siteLanguage}>
        <Header />
        <Main/>
      </ContextUser.Provider>

      <Footer/>
    </div>
  );
}

export default App;
