import React from 'react'
import BasketItem from './BasketItem'

export default function BasketList(props) {
    const { order, showBasket, deleteCardFromBasket,totalPrice } = props
    return (
        <div className='bsk' onClick={showBasket}>
            <div className='container basketlist hoverable' onClick={e => e.stopPropagation(e)} >
                <div className='row'>
                    <i onClick={showBasket} className="fa-solid fa-xmark close"></i>
                    <div className="col l3 m4 s8" >
                        {
                            order.length ?
                                <div>
                                    <table className='responsive-table striped highlight'>
                                        <thead >
                                            <tr className='centered' >
                                                <th>No</th>
                                                <th>Product</th>
                                                <th>action</th>
                                                <th>Price</th>
                                                <th>Delete</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {
                                                order.map((item, index) => {
                                                    return (
                                                        <BasketItem
                                                            index={index}
                                                            key={item.id}
                                                            deleteCardFromBasket={deleteCardFromBasket}
                                                            decrementQuantity={props.decrementQuantity}
                                                            incrementQuantity={props.incrementQuantity}
                                                            {...item}
                                                        />
                                                    )
                                                })
                                            }
                                        </tbody>
                                    </table>
                                    <p className='totalPrice' style={{display:'flex',justifyContent:'right'}}><span>Total Price:</span><button className='btn blue darken-3' disabled>{totalPrice}</button></p>
                                </div>
                                : <h4>Items not found</h4>
                        }
                    </div>
                </div>

            </div>
        </div>

    )
}
