import React, { useEffect, useState } from 'react'
import { API_KEY, API_URL } from '../config';

export default function Main() {
    const [loading, setLoading] = useState(false);
    const [goods, setGoods] = useState([]);

    useEffect(() => {
        setLoading(true)
        fetch(API_URL, {
            headers: {
                Authorization: API_KEY
            }
        })
            .then(res => res.json())
            .then(data => setGoods(data.featured))

        setLoading(false)
    }, []);
    console.log(goods)
    return (
    <div className='main container-fluid ' >
        <div className="row">
            {
                goods.map((item) => {
                    return (

                        <div className="col s12 m4 l3">
                            <div className="card">
                                <div className="card-image " style={{ width: '250px',height:'300px' }}>
                                    <img style={{ width:`calc(100% - 6px)`, height:'100%',padding:'0',boxSizing:'border-box'}} src={item.full_background} alt="shop" />
                                    <a href='#!' className="btn-floating halfway-fab waves-effect waves-light red"><i className="material-icons">add</i></a>
                                </div>
                                <div className="card-content">
                                    <p>I am a very simple card. I am good at containing small bits of information. I am convenient because I require little markup to use effectively.</p>
                                </div>
                            </div>
                        </div>

                    )
                })
            }
        </div>
    </div>
    )
}
