import React from 'react'

export default function BasketItem(props) {
    const {id,name,index,price=0,quantity=0,deleteCardFromBasket}=props
  return ( 
        <tr>
            <td>{index+1}</td>
            <td>{name}</td>
            <td><button onClick={()=>deleteCardFromBasket(id)}><i className="fa-solid fa-plus"  ></i></button><span>{quantity}</span><button onClick={()=>decrementQuantity(id)}><i className="fa-solid fa-minus"></i></button></td>
            <td>{price*quantity}</td>
            <td><i onClick={()=>deleteCardFromBasket(id)} className="fa-solid fa-trash-can"></i></td>
        </tr>
  )
}
